CREATE FUNCTION `partner_keszlete`(`p_id_p` INT,
	`t_id_p` INT,
	`tar_id_p` INT,
	`tort_p` TINYINT
) RETURNS double
    DETERMINISTIC
BEGIN
DECLARE amenny DECIMAL(12,2);
